// Challenge / Exercise
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import Root from "./components/RootLayout.js";
import Home from "./page/Homepage.js";
import EventDetails, {loader as eventDetailsLoader} from "./page/EventDetailsPage.js";
import NewEvent from "./page/NewEventPage.js";
import Events, { loader } from "./page/Eventspage.js";
import EditEvent from "./page/EditEventPage.js";

import EventsRootLayout from "./page/EventsRoot.js";
import ErrorPage from "./page/Error.js";
// BONUS: Add another (nested) layout route that adds the <EventNavigation> component above all /events... page components

const router = createBrowserRouter([
  {
    path: "/",
    element: <Root />,
    errorElement: <ErrorPage />,
    // Error will be bubbled up
    children: [
      { index: true, element: <Home /> },
      {
        path: "events",
        element: <EventsRootLayout />,
        children: [
          {
            index: true,
            element: <Events />,
            loader: loader
          },
          { path: ":eventId",
            id: 'event-details',
            loader: eventDetailsLoader,
            children: [
              {index:true, element: <EventDetails />},
              {path: 'edit', element: <EditEvent />}
            ]

          },
          { path: "new", element: <NewEvent /> },
        ],
      },
    ],
  },
]);

function App() {
  return <RouterProvider router={router} />;
}

export default App;
